int main()
{
	// Initialize GLFW
	glfwInit();

	//  Using OpenGL 3.3
	glfwWindowHint(GLFW_CONTEXT_VERSION_MAJOR, 3);
	glfwWindowHint(GLFW_CONTEXT_VERSION_MINOR, 3);

	// Using CORE profile
	glfwWindowHint(GLFW_OPENGL_PROFILE, GLFW_OPENGL_CORE_PROFILE);

	// Create a window
	GLFWwindow* window = glfwCreateWindow(width, height, "  ", NULL, NULL);

	// Error check if the window fails to create
	if (window == NULL)
	{
		std::cout << "Failed to create GLFW window" << std::endl;
		glfwTerminate();
		return -1;
	}

	// Introduce the window into the current context
	glfwMakeContextCurrent(window);

	// If the window size is changed, the framebuffer_size_callback function will be called
	glfwSetFramebufferSizeCallback(window, framebuffer_size_callback);

	//Load GLAD so it configures OpenGL
	gladLoadGL();

	// Specify the viewport of OpenGL in the Window
	glViewport(0, 0, width, height);

	// Texture data
	Texture textures[]
	{
		Texture("Textures/bracelet.png", "diffuse", 0, GL_RGBA, GL_UNSIGNED_BYTE),
	};

	// Generates Shader object
	Shader shaderProgram("Shaders/default.vert", "Shaders/default.frag");

	// Store mesh data in vectors for the mesh
	std::vector <Vertex> verts(braceletVertices, braceletVertices + sizeof(braceletVertices) / sizeof(Vertex));
	std::vector <GLuint> ind(indices, indices + sizeof(indices) / sizeof(GLuint));
	std::vector <Texture> tex(textures, textures + sizeof(textures) / sizeof(Texture));

	// Create bracelet mesh
	Mesh bracelet(verts, ind, tex);

	// Shader for light (cube with one color)
	Shader lightShader("Shaders/light.vert", "Shaders/light.frag");

	// Store mesh data in vectors for the mesh
	std::vector <Vertex> lightVerts(lightVertices, lightVertices + sizeof(lightVertices) / sizeof(Vertex));
	std::vector <GLuint> lightInd(lightIndices, lightIndices + sizeof(lightIndices) / sizeof(GLuint));

	// Create light mesh
	Mesh light(lightVerts, lightInd, tex);

	// Set world coordinate for light
	glm::vec4 lightColor = glm::vec4(1.0f, 1.0f, 1.0f, 1.0f);
	glm::vec3 lightPos = glm::vec3(0.0, -0.7f, 3.5f);
	glm::mat4 lightModel = glm::mat4(1.0f);
	lightModel = glm::translate(lightModel, lightPos);

	// Set world coordinate for bracelet
	glm::vec3 objectPos = glm::vec3(0.0f, 0.0f, 1.7f);
	glm::mat4 objectModel = glm::mat4(1.0f);
	glm::mat4 objectModel2 = glm::mat4(1.0f);
	objectModel = glm::translate(objectModel, objectPos);

	// Set color for light
	lightShader.Activate();
	glUniformMatrix4fv(glGetUniformLocation(lightShader.ID, "model"), 1, GL_FALSE, glm::value_ptr(lightModel));
	glUniform4f(glGetUniformLocation(lightShader.ID, "lightColor"), lightColor.x, lightColor.y, lightColor.z, lightColor.w);

	// Configure light for object
	shaderProgram.Activate();
	glUniformMatrix4fv(glGetUniformLocation(shaderProgram.ID, "model"), 1, GL_FALSE, glm::value_ptr(objectModel));
	glUniform4f(glGetUniformLocation(shaderProgram.ID, "lightColor"), lightColor.x, lightColor.y, lightColor.z, lightColor.w);
	glUniform3f(glGetUniformLocation(shaderProgram.ID, "lightPos"), lightPos.x, lightPos.y, lightPos.z);

	// For multiple light
	//glUniform3f(glGetUniformLocation(shaderProgram.ID, "lightsPos[0]"), lightPos.x, lightPos.y, lightPos.z);
	//glUniform3f(glGetUniformLocation(shaderProgram.ID, "lightsPos[1]"), lightPos.x-0.5, lightPos.y-0.2, lightPos.z+0.5);
	
	// Enables the Depth Buffer
	glEnable(GL_DEPTH_TEST);

	// Enables blending
	glEnable(GL_BLEND);
	// Blending function
	glBlendFunc(GL_SRC_ALPHA, GL_ONE_MINUS_SRC_ALPHA);

	// Enables drawing points
	//glEnable(GL_POINT_SIZE);
	// Enables drawing points with different size of points
	//glEnable(GL_PROGRAM_POINT_SIZE);
	// To drawing circular points
	//glHint(GL_POINT_SMOOTH_HINT, GL_NICEST);

	// Creates camera object
	Camera camera(width, height, glm::vec3(0.0f, 0.0f, 2.0f));
	shaderProgram.Activate();
	objectModel = glm::rotate(objectModel, glm::radians(100.0f), glm::vec3(1.0f, 0.0f, 0.0f));
	glUniformMatrix4fv(glGetUniformLocation(shaderProgram.ID, "model"), 1, GL_FALSE, glm::value_ptr(objectModel));
	double lastTime = glfwGetTime();
	int nbFrames = 0;

	// Enables vsync
	//glfwSwapInterval(1);

	// Main while loop
	while (!glfwWindowShouldClose(window))
	{
		// Specify the color of the background
		glClearColor(0.07f, 0.13f, 0.17f, 1.0f);

		// Clean the back buffer and depth buffer
		glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT);

		double currentTime = glfwGetTime();
		double timeDiff = currentTime - lastTime;
		nbFrames++;
		if (timeDiff >= 1.0 / 30.0) {
			// Rotate object 
			shaderProgram.Activate();
			objectModel = glm::rotate(objectModel, glm::radians((float)20.0f* (float)timeDiff), glm::vec3(0.0f, 0.0f, 1.0f));
			glUniformMatrix4fv(glGetUniformLocation(shaderProgram.ID, "model"), 1, GL_FALSE, glm::value_ptr(objectModel));

			// Display FPS
			std::stringstream ss;
			ss << "FPS: " << (1.0 / timeDiff) * nbFrames;
			glfwSetWindowTitle(window, ss.str().c_str());
			nbFrames = 0;
			lastTime = currentTime;
		}



		// Handles camera inputs
		camera.Inputs(window);
		// Updates and exports the camera matrix to the Vertex Shader
		camera.updateMatrix(45.0f, 0.01f, 100.0f);

		// Draws different meshes
		light.Draw(lightShader, camera);
		bracelet.Draw(shaderProgram, camera);

		
		// Draws the object five times, each with a different rotation
		//shaderProgram.Activate();
		//for (int i=0 ;i<5;i++){
		//	objectModel2 = glm::rotate(objectModel, glm::radians(10.0f*i), glm::vec3(0.0f, 1.0f, 0.0f));
		//	objectModel2 = glm::scale(objectModel2, glm::vec3(.998f, .9998f, 1.0f));
		//	glUniformMatrix4fv(glGetUniformLocation(shaderProgram.ID, "model"), 1, GL_FALSE, glm::value_ptr(objectModel2));
		//	bracelet.Draw(shaderProgram, camera);
		//}

		// Swap the back buffer with the front buffer
		glfwSwapBuffers(window);

		// Take care of all GLFW events
		glfwPollEvents();
	}
	// Delete all the objects we've created
	shaderProgram.Delete();
	lightShader.Delete();
	// Delete window before ending the program
	glfwDestroyWindow(window);
	// Terminate GLFW before ending the program
	glfwTerminate();
	return 0;
}